import React, { useEffect, useState } from 'react'
import { useFilePicker } from 'use-file-picker';
import profileImg from "../images/default_profile.jpg"
import background1 from "../images/background4.jpg"

const Register = () => {

    const [windowSize, updateWindowSize] = useState(window.innerWidth)
    const [adminObj, updateAdmin] = useState({
        username: "",
        email: "",
        password: "",
        image:""
    })

    const [values, setValues] = useState({
        imagePreviewUrl: "", 
        picFile: null
     })

     let fileInput = React.createRef(); 

    const [openFileSelector, { filesContent, loading, errors }] = useFilePicker({
        readAs: 'DataURL',
        accept: '.image/*',
        multiple: true,
        limitFilesConfig: { max: 1 },
        // minFileSize: 0.1, // in megabytes
        maxFileSize: 50,
        imageSizeRestrictions: {
          maxHeight: 900, // in pixels
          maxWidth: 1600,
          minHeight: 600,
          minWidth: 768,
        },
      });

   

    useEffect(()=>{
        window.addEventListener("resize", ()=>{
            updateWindowSize(window.innerWidth)
        })
    },[windowSize])

  /*   const style = {
        backgroundImage: windowSize > 350 ? `url(${background1})` : "black"
    } */

    const handleInput = (e) =>{
        updateAdmin((oldData)=>{
            const {type} = e.target.type
            return {
                ...oldData,
                [e.target.name]:e.target.value}
            })

        }
    
       // Activates user file input to set div
   const editProfilePic = () => {
    fileInput.current.click();
 } 
 // Handles the image that was input by user
 const handleImageChange = e => {
    e.preventDefault();
    let reader = new FileReader();
    let inFile = e.target.files[0];
    reader.onloadend = () => {
       setValues({...values, 
          picFile: inFile, 
          imagePreviewUrl: reader.result
       })
    };
reader.readAsDataURL(inFile);
 };

    console.log(adminObj)
    console.log(filesContent)
  //  console.log(filesContent[0].content)

  

  return (
    <div className='app row horz-center vert-center' style={{backgroundImage:`url(${background1})`}}>
        <form className='form column vert-center sm-gap' enctype="multipart/form-data">
            <div type="file" className='add-image row' onClick={()=> editProfilePic()}>
                 <img src={values.imagePreviewUrl}></img>
                 <p className='sm-text'>Add Image</p>
                 <input 
           type="file"
           accept="image/*"
           onChange={handleImageChange}
           ref={fileInput} />
            </div>
            <input type="file" name="image" onClick={handleInput}  />
            
            <input type="text" placeholder='username' name ="username" onChange={handleInput} />
            <input type="email" placeholder='email' name="email" onChange={handleInput} />
            <input type="password"  id="" placeholder='123' name="password" onChange={handleInput} />
            <button>Register</button>
        </form>
    </div>
  )
}


export default Register
