import { createSlice } from "@reduxjs/toolkit";


const adminSlice = createSlice({
    name: "Admin",
    initialState: {
        currentAdmin: {}
    },
    reducers : {
        storeAdmin: (action, state)=>{
            state.currentAdmin = action.payload
        }
    }
})

export const { storeAdmin } = adminSlice.actions;
export default adminSlice.reducer;
